import { Link } from "react-router-dom";
import { useState, useEffect } from "react";
import axios from "axios";
import "./product.css";
import { Publish } from "@material-ui/icons";
import storage from "../../firebase";

export default function Product() {
    const id = window.location.pathname.split('/').pop();
    const [movie, setMovie] = useState({});
    const [updatedMovie, setUpdatedMovie] = useState({});
    const [img, setImg] = useState(null);
    const [imgTitle, setImgTitle] = useState(null);
    const [imgSm, setImgSm] = useState(null);
    const [trailer, setTrailer] = useState(null);
    const [video, setVideo] = useState(null);
    const [uploaded, setUploaded] = useState(0);

    useEffect(() => {
        const fetchMovie = async () => {
            try {
                const res = await axios.get(`/movies/find/${id}`);
                setMovie(res.data);
                setUpdatedMovie(res.data);
            } catch (error) {
                console.error("Error fetching movie:", error);
            }
        };

        fetchMovie();
    }, [id]);

    const handleChange = (e) => {
        const value = e.target.value;
        setUpdatedMovie({ ...updatedMovie, [e.target.name]: value });
    };

    const upload = (file, label) => {
        const fileName = new Date().getTime() + label + file.name;
        const uploadTask = storage.ref(`/items/${fileName}`).put(file);
        uploadTask.on(
            "state_changed",
            (snapshot) => {
                const progress =
                    (snapshot.bytesTransferred / snapshot.totalBytes) * 100;
                console.log("Upload is " + progress + "% done");
            },
            (error) => {
                console.log(error);
            },
            () => {
                uploadTask.snapshot.ref.getDownloadURL().then((url) => {
                    setUpdatedMovie((prev) => {
                        return { ...prev, [label]: url };
                    });
                    setUploaded((prev) => prev + 1);
                });
            }
        );
    };

    const handleUpload = (e) => {
        e.preventDefault();
        upload(img, "img");
        upload(imgTitle, "imgTitle");
        upload(imgSm, "imgSm");
        upload(trailer, "trailer");
        upload(video, "video");
    };

    const handleUpdate = async (e) => {
        e.preventDefault();
        try {
            await axios.put(`/movies/${id}`, updatedMovie);
            // Handle success or navigate to a success page
        } catch (error) {
            console.error("Error updating movie:", error);
        }
    };

    return (
        <div className="product">
            <div className="productTitleContainer">
                <h1 className="productTitle">Movie</h1>
                <Link to="/newproduct">
                    <button className="productAddButton">Create</button>
                </Link>
            </div>
            <div className="productTop">
                <div className="productTopRight">
                    <div className="productInfoTop">
                        <img src={movie.img} alt="" className="productInfoImg" />
                        <span className="productName">{movie.title}</span>
                    </div>
                    <div className="productInfoBottom">
                        <div className="productInfoItem">
                            <span className="productInfoKey">ID:</span>
                            <span className="productInfoValue">{movie._id}</span>
                        </div>
                        <div className="productInfoItem">
                            <span className="productInfoKey">Genre:</span>
                            <span className="productInfoValue">{movie.genre}</span>
                        </div>
                        <div className="productInfoItem">
                            <span className="productInfoKey">Year:</span>
                            <span className="productInfoValue">{movie.year}</span>
                        </div>
                        <div className="productInfoItem">
                            <span className="productInfoKey">Limit:</span>
                            <span className="productInfoValue">{movie.limit}</span>
                        </div>
                    </div>
                </div>
            </div>
            <div className="productBottom">
                <form className="productForm">
                    <div className="productFormLeft">
                        <label>Movie Title</label>
                        <input type="text" placeholder={movie.title} name="title" onChange={handleChange} />
                        <label>Year</label>
                        <input type="text" placeholder={movie.year} name="year" onChange={handleChange} />
                        <label>Genre</label>
                        <input type="text" placeholder={movie.genre} name="genre" onChange={handleChange} />
                        <label>Limit</label>
                        <input type="text" placeholder={movie.limit} name="limit" onChange={handleChange} />
                        <label>Trailer</label>
                        <input type="file" name="trailer" onChange={(e) => setTrailer(e.target.files[0])} />
                        <label>Video</label>
                        <input type="file" name="video" onChange={(e) => setVideo(e.target.files[0])} />
                    </div>
                    <div className="productFormRight">
                        <div className="productUpload">
                            <img src={movie.img} alt="" className="productUploadImg" />
                            <label htmlFor="file">
                                <Publish />
                            </label>
                            <input type="file" id="file" style={{ display: "none" }} />
                        </div>
                        <button className="productButton" onClick={handleUpdate}>Update</button>
                    </div>
                </form>
            
                    <button className="productButton" onClick={handleUpload}>Upload</button>
               
            </div>
        </div>
    );
}
